import { useEffect, useState, useContext } from "react";
import SearchBox from "./Components/SearchBox";


//import data from "../../data/user.json";
import "./style.css";
import SearchResults from "./Components/SearchResults";
import PokemonContext from "../../context/pokemons";

export default function Search() { 
    const [isAtTop, setIsAtTop] = useState(false);
    const [results, setResults] = useState([]);
    const [data, setData] = useState([]);
    const { getPokemons, currents, newsl } = useContext(PokemonContext);

    // useEffect(() => {
    //     const getUsers = async () => {
    //         try{
    //             //const response = await fetch("https://jsonplaceholder.typicode.com/users")
    //             const response = await fetch("https://localhost:44338/BuscarCiudades/City/manizales")
    //             const data = await response.json();
    //             setData(data);   
    //         } catch(err){
    //             console.error(err);
    //         }                     
    //     };

    //     getUsers().catch(null);
    // }, []);

    const handleCloseOpenSearch = () => 
    {
        setIsAtTop(false);
        setResults([]);
    };        

    const handleSearchClick = (searchText) => {                
        setIsAtTop(true);
        getPokemons(searchText).catch(null);
        console.log(currents);      
        if(data.length){    
            console.log(data?.length);        
            const searchTextMinus = searchText.toLowerCase();
            const filteredData = data.currents.filter((value) => (                
                value.name.toLowerCase().includes(searchTextMinus) || 
                value.phone.toLowerCase().includes(searchTextMinus) ||
                value.email.toLowerCase().includes(searchTextMinus) ||
                value.username.toLowerCase().includes(searchTextMinus)                
            )
            );
            setResults(filteredData);
        }
    };
    //console.log(results);
    return (
        <div className={`search ${isAtTop ? "search--top" : "search--center"}`}>
            <SearchBox 
            onSearch={handleSearchClick} 
            onClose={handleCloseOpenSearch}
            isSearching={isAtTop}
            />
            <SearchResults results={results} isSearching={isAtTop}/>
        </div>
    );
}
