import { Children, useState } from "react";
import PokemonContext from "./index";
import apiCall from "../../api";

export default function PokemonProvider({ children }){
  const [currents, setCurrents] = useState([]);
  const [news, setNews] = useState([]);
  const [searchc, setSearchc] = useState("");

  const getPokemons = async (searchc) =>{
    try{
      const pokemonsResult = await apiCall({ url: "https://localhost:44338/BuscarCiudades/City/"+searchc })
      console.log(searchc);
      setCurrents(pokemonsResult.current_weather);
      setNews(pokemonsResult.news.value);
    } catch(error){
      setCurrents([]);
    }
  };
  return(
      <PokemonContext.Provider value={{ getPokemons, currents, news }}>
          {children}
      </PokemonContext.Provider>
  );
}
