import { useEffect, useState, useContext } from "react";
import SearchBox from "./Components/SearchBox";
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';


//import data from "../../data/user.json";
import "./style.css";
import SearchResults from "./Components/SearchResults";
import PokemonContext from "../../context/pokemons";

export default function Search() { 
    const [isAtTop, setIsAtTop] = useState(false);
    const [noticias, setNoticias] = useState([]);
    const [ciudades, setCiudades] = useState([]);
    const [mensaje, setMensaje] = useState("");
    //const { getPokemons, currents, newsl } = useContext(PokemonContext);

    // useEffect(() => {
    //     const getUsers = async () => {
    //         try{
    //             //const response = await fetch("https://jsonplaceholder.typicode.com/users")
    //             const response = await fetch("https://localhost:44338/BuscarCiudades/City/manizales")
    //             const data = await response.json();
    //             setData(data);   
    //         } catch(err){
    //             console.error(err);
    //         }                     
    //     };

    //     getUsers().catch(null);
    // }, []);

    const [open, setOpen] = React.useState(false);
    const handleClose = () => {
        setOpen(false);
    };
    const handleToggle = () => {
        setOpen(!open);
    };

    const handleCloseOpenSearch = () => 
    {
        setIsAtTop(false);
        setCiudades([]);
        setNoticias([]);
        setMensaje("");
    };   
    
    const handleBuscarCiudad = (searchText) => {
        setIsAtTop(true);   
        const getCiudades = async () => {
                    try{
                        const response = await fetch("https://localhost:44338/BuscarCiudades/City/"+searchText)
                        const data_ = await response.json();
                        setCiudades(data_.current_weather); 
                        setNoticias(data_.news.value);
                        setMensaje("");
                    } catch(err){                        
                        setCiudades([]);
                        setNoticias([]);
                        console.error(err);
                        setMensaje("NO SE ENCONTRARON RESULTADOS");
                    }                     
                };                             
                getCiudades().catch(null);
    };

    //console.log(noticias);
    
    return (
        <div className={`search ${isAtTop ? "search--top" : "search--center"}`}>
            <SearchBox 
            onSearch={handleBuscarCiudad} 
            onClose={handleCloseOpenSearch}
            isSearching={isAtTop}
            />
            <Backdrop
                sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open={open}
                onClick={handleClose}
            >
                <CircularProgress color="inherit" />
            </Backdrop>
            <SearchResults results={ciudades} isSearching={isAtTop} lnoticias={noticias} mensajerror={mensaje}/>
        </div>
    );
}
