import { useEffect, useState, useContext } from "react";
import SearchBox from "./Components/SearchBox";
import * as React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';


//import data from "../../data/user.json";
import "./style.css";
import SearchResults from "./Components/SearchResults";
import PokemonContext from "../../context/pokemons";

export default function Search() { 
    const [isAtTop, setIsAtTop] = useState(false);
    const [noticias, setNoticias] = useState([]);
    const [ciudades, setCiudades] = useState([]);
    //const { getPokemons, currents, newsl } = useContext(PokemonContext);

    // useEffect(() => {
    //     const getUsers = async () => {
    //         try{
    //             //const response = await fetch("https://jsonplaceholder.typicode.com/users")
    //             const response = await fetch("https://localhost:44338/BuscarCiudades/City/manizales")
    //             const data = await response.json();
    //             setData(data);   
    //         } catch(err){
    //             console.error(err);
    //         }                     
    //     };

    //     getUsers().catch(null);
    // }, []);

    const handleCloseOpenSearch = () => 
    {
        setIsAtTop(false);
        setCiudades([]);
        setNoticias([]);
    };   
    
    const handleBuscarCiudad = (searchText) => {
        setIsAtTop(true);   
        const getCiudades = async () => {
                    try{
                        const response = await fetch("https://localhost:44338/BuscarCiudades/City/"+searchText)
                        const data_ = await response.json();
                        setCiudades(data_.current_weather); 
                        setNoticias(data_.news.value);
                    } catch(err){                        
                        setCiudades([]);
                        setNoticias([]);
                        console.error(err);
                    }                     
                };                             
                getCiudades().catch(null);
    };

    //console.log(noticias);
    
    return (
        <div className={`search ${isAtTop ? "search--top" : "search--center"}`}>
            <SearchBox 
            onSearch={handleBuscarCiudad} 
            onClose={handleCloseOpenSearch}
            isSearching={isAtTop}
            />
            <SearchResults results={ciudades} isSearching={isAtTop} lnoticias={noticias}/>
            <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary="Brunch this weekend?"
          secondary={
            <React.Fragment>
              <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="body2"
                color="text.primary"
              >
                Ali Connors
              </Typography>
              {" — I'll be in your neighborhood doing errands this…"}
            </React.Fragment>
          }
        />
      </ListItem>
      <Divider variant="inset" component="li" />
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary="Summer BBQ"
          secondary={
            <React.Fragment>
              <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="body2"
                color="text.primary"
              >
                to Scott, Alex, Jennifer
              </Typography>
              {" — Wish I could come, but I'm out of town this…"}
            </React.Fragment>
          }
        />
      </ListItem>
      <Divider variant="inset" component="li" />
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg" />
        </ListItemAvatar>
        <ListItemText
          primary="Oui Oui"
          secondary={
            <React.Fragment>
              <Typography
                sx={{ display: 'inline' }}
                component="span"
                variant="body2"
                color="text.primary"
              >
                Sandra Adams
              </Typography>
              {' — Do you have Paris recommendations? Have you ever…'}
            </React.Fragment>
          }
        />
      </ListItem>
    </List>
        </div>
    );
}
