import {Button} from "@mui/material";
import { useState } from "react";
import "./style.css";

export default function SearchBox({ onSearch, onClose, isSearching}) {
    const [searchText, setSearchText] = useState("");

    const handleSearchClick = () => {
        setSearchText("");
        onClose();
    }

    return(
    <div className="search-box">
        <h2 className="search-box-titulo">Buscador de Ciudades</h2>
        <div className="search-box-buttons">
            <label>
                <input 
                value={searchText} 
                onChange={({ target: { value }}) => setSearchText(value)}
                className="search-box-input"
                />
            </label>           
            <Button variant="contained" onClick={() => onSearch(searchText)} disabled={!searchText.length}>Buscar</Button>
            {isSearching && <Button onClick={handleSearchClick} disabled={!searchText.length}>Cerrar</Button>}
        </div>        
    </div>
    );
}
