import { useEffect, useState, useContext } from "react";
import SearchBox from "./Components/SearchBox";


//import data from "../../data/user.json";
import "./style.css";
import SearchResults from "./Components/SearchResults";
import PokemonContext from "../../context/pokemons";

export default function Search() { 
    const [isAtTop, setIsAtTop] = useState(false);
    const [msjErrors, setMsjErrors] = useState(false);
    const [results, setResults] = useState([]);
    const [ciudades, setCiudades] = useState([]);
    const { getPokemons, currents, newsl } = useContext(PokemonContext);

    // useEffect(() => {
    //     const getUsers = async () => {
    //         try{
    //             //const response = await fetch("https://jsonplaceholder.typicode.com/users")
    //             const response = await fetch("https://localhost:44338/BuscarCiudades/City/manizales")
    //             const data = await response.json();
    //             setData(data);   
    //         } catch(err){
    //             console.error(err);
    //         }                     
    //     };

    //     getUsers().catch(null);
    // }, []);

    const handleCloseOpenSearch = () => 
    {
        setIsAtTop(false);
        setCiudades([]);
    };   
    
    const handleBuscarCiudad = (searchText) => {
        setIsAtTop(true);   
        const getCiudades = async () => {
                    try{
                        const response = await fetch("https://localhost:44338/BuscarCiudades/City/"+searchText)
                        const data_ = await response.json();
                        setCiudades(data_.current_weather);  
                        setMsjErrors(false); 
                    } catch(err){
                        console.error(err.message);
                        setMsjErrors(true); 
                    }                     
                };
                getCiudades().catch(null);                                            
    };
    
    return (
        <div className={`search ${isAtTop ? "search--top" : "search--center"}`}>
            <SearchBox 
            onSearch={handleBuscarCiudad} 
            onClose={handleCloseOpenSearch}
            isSearching={isAtTop}
            />
            <SearchResults results={ciudades} isSearching={isAtTop} ErrorsMsj={msjErrors}/>
        </div>
    );
}
