import { useEffect, useState, useContext } from "react";
import SearchBox from "./Components/SearchBox";
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';

//import data from "../../data/user.json";
import "./style.css";
import SearchResults from "./Components/SearchResults";
import PokemonContext from "../../context/pokemons";


const drawerWidth = 240;

export default function Search() { 
    const [isAtTop, setIsAtTop] = useState(false);
    const [noticias, setNoticias] = useState([]);
    const [ciudades, setCiudades] = useState([]);
    const [mensaje, setMensaje] = useState("");
    //const { getPokemons, currents, newsl } = useContext(PokemonContext);

    // useEffect(() => {
    //     const getUsers = async () => {
    //         try{
    //             //const response = await fetch("https://jsonplaceholder.typicode.com/users")
    //             const response = await fetch("https://localhost:44338/BuscarCiudades/City/manizales")
    //             const data = await response.json();
    //             setData(data);   
    //         } catch(err){
    //             console.error(err);
    //         }                     
    //     };

    //     getUsers().catch(null);
    // }, []);

    const [open, setOpen] = useState(false);
    const handleClose = () => {
        setOpen(false);
    };
    const handleToggle = () => {
        setOpen(!open);
    };

    const handleCloseOpenSearch = () => 
    {
        setIsAtTop(false);
        setCiudades([]);
        setNoticias([]);
        setMensaje("");
    };   
    
    const handleBuscarCiudad = (searchText) => {
        setOpen(true);
        setIsAtTop(true);   
        const getCiudades = async () => {
                    try{
                        const response = await fetch("https://localhost:44338/BuscarCiudades/City/"+searchText)
                        const data_ = await response.json();
                        setCiudades(data_.current_weather); 
                        setNoticias(data_.news.value);
                        setMensaje("");
                        setOpen(false);
                    } catch(err){                        
                        setCiudades([]);
                        setNoticias([]);
                        console.error(err);
                        setMensaje("NO SE ENCONTRARON RESULTADOS");
                        setOpen(false);
                    }                     
                };                             
                getCiudades().catch(null);
    };

    //console.log(noticias);
    
    return (
        <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            Clima Y Noticias
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          <List>
            {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
              <ListItem button key={text}>
                <ListItemIcon>
                  {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
          <Divider />
          <List>
            {['All mail', 'Trash', 'Spam'].map((text, index) => (
              <ListItem button key={text}>
                <ListItemIcon>
                  {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <div className={`search ${isAtTop ? "search--top" : "search--center"}`}>
            <SearchBox 
            onSearch={handleBuscarCiudad} 
            onClose={handleCloseOpenSearch}
            isSearching={isAtTop}
            />
            <Backdrop
                sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                open={open}
                onClick={handleClose}
            >
                <CircularProgress color="inherit" />
            </Backdrop>
            <SearchResults results={ciudades} isSearching={isAtTop} lnoticias={noticias} mensajerror={mensaje}/>
        </div>
      </Box>
    </Box>        
    );
}
